import React, { Component } from "react";

import { Map, View, Overlay } from "ol";
import VectorLayer from "ol/layer/Vector";
import VectorSource from "ol/source/Vector";
import VectorTileSource from "ol/source/VectorTile";
import VectorTileLayer from "ol/layer/VectorTile";
import TopoJSON from "ol/format/TopoJSON";
import TileLayer from "ol/layer/Tile";
import OSM from "ol/source/OSM";
import { fromLonLat } from "ol/proj";
import "ol/style";
import "./OpticMap.css";

class OpticMap extends Component {
  componentDidMount() {
    // create feature layer and vector source
    // var featuresLayer = new VectorLayer({
    //   source: new VectorSource({
    //     features: []
    //   })
    // });

    const narvaWebMercator = fromLonLat([28.16804, 59.37911]);
    // create map object with feature layer
    var map = new Map({
      layers: [
        new VectorTileLayer({
          source: new VectorTileSource({
            attributions:
              "&copy; OpenStreetMap contributors, Who’s On First, " +
              "Natural Earth, and openstreetmapdata.com",
            format: new TopoJSON({
              layerName: "layer",
              layers: ["water", "roads", "buildings"]
            }),
            maxZoom: 19
          })
        })
      ],
      target: this.refs.mapContainer,
      view: new View({
        center: fromLonLat([-74.0064, 40.7142]),
        maxZoom: 19,
        zoom: 15
      })
      // target: this.refs.mapContainer,
      // layers: [
      //   //default OSM layer
      //   new VectorTileLayer({
      //     source: new VectorTileSource()
      //   })
      //   // ,
      //   // featuresLayer
      // ],
      // view: new View({
      //   center: narvaWebMercator, //Boulder
      //   zoom: 12
      // })
    });

    // save map and layer references to local state
    this.setState({
      map: map
      // featuresLayer: featuresLayer
    });
  }

  // pass new features from props into the OpenLayers layer object
  // componentDidUpdate(prevProps, prevState) {
  //   this.state.featuresLayer.setSource(
  //     new VectorSource({
  //       features: this.props.routes
  //     })
  //   );
  // }

  render() {
    return (
      <div className="mapContainer" id="mapContainer" ref="mapContainer"></div>
    );
  }
}

export default OpticMap;
