import React, { Component } from "react";

import ol from "ol";

class OpticMap extends Component {
  render() {
    return <div ref="mapContainer"> </div>;
  }
  componentDidMount() {
    // create feature layer and vector source
    var featuresLayer = new ol.layer.Vector({
      source: new ol.source.Vector({
        features: []
      })
    });

    // create map object with feature layer
    var map = new ol.Map({
      target: this.refs.mapContainer,
      layers: [
        //default OSM layer
        new layer.Tile({
          source: new OSM()
        }),
        featuresLayer
      ],
      view: new View({
        center: [-11718716.28195593, 4869217.172379018], //Boulder, CO
        zoom: 13
      })
    });

    // save map and layer references to local state
    this.setState({
      map: map,
      featuresLayer: featuresLayer
    });
  }
  componentDidUpdate(prevProps, prevState) {
    this.state.featuresLayer.setSource(
      new source.Vector({
        features: this.props.routes
      })
    );
  }
}

export default OpticMap;
