import React, { Component } from "react";

import { Map, View } from "ol";
import TileLayer from "ol/layer/Tile";
import OSM from "ol/source/OSM";
import { fromLonLat, transformExtent, transform } from "ol/proj";
import Style from "ol/style/Style";
import { Circle, Stroke, Fill, Text } from "ol/style";
import "./OpticMap.css";
import Feature from "ol/Feature";
// import Icon from "ol/style/Icon";
// import Polygon from "ol/geom/Polygon";
// import Point from "ol/geom/Point";
// import Group from "ol/layer/Group";
// import VectorContext from "ol/render/VectorContext";
import VectorLayer from "ol/layer/Vector";
import VectorSource from "ol/source/Vector";

class OpticMap extends Component {
  componentDidMount() {
    const extentView = [
      20.969893 /** min x */,
      57.540673 /** min y */,
      28.413477 /** max x */,
      59.894102 /** max y */
    ];
    const extentLayer = [
      16.581885 /** min x */,
      55.671002 /** min y */,
      32.794556 /** max x */,
      61.543774 /** max y */
    ];

    // var feature = new Feature();
    // var geom = new Circle([28.185281, 59.374348], 100, "XY");
    // feature.setGeometry(geom);

    // var style = new Style({
    //   fill: new Fill(),
    //   text: new Text({
    //     text: "hey",
    //     font: "20px sans-serif",
    //     offsetY: -20
    //   })
    // });
    // style.getFill().setColor("rgba(0, 0, 255, 0.7)");

    // let switchesLayer = new VectorLayer({
    //   source: new VectorSource({
    //     features: [feature]
    //   }),
    //   style: style
    // });

    let map = new Map({
      target: this.refs.mapContainer,
      layers: [
        new TileLayer({
          source: new OSM(),
          extent: extentLayer,
          zIndex: 0
        })
      ],
      view: new View({
        center: fromLonLat([28.185281, 59.374348]),
        zoom: 14,
        projection: "EPSG:4326",
        extent: extentView,
        minZoom: 7.5
      })
    });

    const coordinateContainerElement = this.refs.coordinateString;
    this.setState({
      map: map
    });
    // get coordinates by click on map
    map.on("click", function(pointOfClick) {
      const clickedCoordinate = transform(
        pointOfClick.coordinate,
        "EPSG:3857",
        "EPSG:4326"
      );
      coordinateContainerElement.innerHTML =
        clickedCoordinate[0].toFixed(6) + "," + clickedCoordinate[1].toFixed(6);
    });
  }

  render() {
    return (
      <div className="opticMapComponent">
        <div className="leftMapPanel">
          <div
            className="coordinateString"
            id="coordinateString"
            ref="coordinateString"
          >
            coordinates
          </div>
          <div className="featureLayers">
            <div className="layerCheckBox">
              <input
                type="checkbox"
                name="featureLayerCheckbox"
                value="switchesLayer"
                className="switch"
              />
              <span className="slider"></span>
              <div>switches</div>
            </div>

            <div className="layerCheckBox">
              <input
                type="checkbox"
                name="featureLayerCheckbox"
                value="clutchesLayer"
                className="switch"
              />
              <span className="slider"></span>
              <div>optic clutches</div>
            </div>

            <div className="layerCheckBox">
              <input
                type="checkbox"
                name="featureLayerCheckbox"
                value="cableLayer"
                className="switch"
              />
              <span className="slider"></span>
              <div>optic Cable</div>
            </div>
          </div>
        </div>
        <div
          className="mapContainer"
          id="mapContainer"
          ref="mapContainer"
        ></div>
        <div className="rightMapPanel"></div>
      </div>
    );
  }
}

export default OpticMap;
