import React, { Component } from "react";

import { Map, View } from "ol";
import TileLayer from "ol/layer/Tile";
import OSM from "ol/source/OSM";
import { fromLonLat, transformExtent } from "ol/proj";
import "ol/style";
import "./OpticMap.css";

class OpticMap extends Component {
  componentDidMount() {
    const narvaWebMercator = fromLonLat([28.16804, 59.37911]);
    const extent = transformExtent(
      [58.971672, 26.700827, 59.470694, 28.211319],
      "EPSG:4326",
      "EPSG:3857"
    );
    let map = new Map({
      target: this.refs.mapContainer,
      layers: [
        new TileLayer({
          source: new OSM()
        })
      ],
      view: new View({
        center: narvaWebMercator,
        zoom: 11,
        minZoom: 10,
        extent: extent
      })
    });

    this.setState({
      map: map
    });
  }

  render() {
    return (
      <div className="opticMapComponent">
        <div className="leftMapPanel"></div>
        <div
          className="mapContainer"
          id="mapContainer"
          ref="mapContainer"
        ></div>
        <div className="rightMapPanel"></div>
      </div>
    );
  }
}

export default OpticMap;
