import React, { Component } from "react";

import { Map, View } from "ol";
import TileLayer from "ol/layer/Tile";
import OSM from "ol/source/OSM";
import { fromLonLat, transformExtent } from "ol/proj";
import "ol/style";
import "./OpticMap.css";

class OpticMap extends Component {
  componentDidMount() {
    const narvaWebMercator = fromLonLat([27.428139, 59.239859]);
    const extent = transformExtent(
      [
        26.700827 /** min x */,
        58.971672 /** min y */,
        28.211319 /** max x */,
        59.470694 /** max y */
      ],
      "EPSG:4326",
      "EPSG:3857"
    );
    let map = new Map({
      target: this.refs.mapContainer,
      layers: [
        new TileLayer({
          source: new OSM()
        })
      ],
      view: new View({
        center: narvaWebMercator,
        zoom: 10,
        extent: extent
      })
    });
    const coordinateContainerElement = this.refs.coordinateString;
    this.setState({
      map: map
    });
    map.on("click", function(pointOfClick) {
      const clickedCoordinate = pointOfClick.coordinate;
      coordinateContainerElement.innerHTML = clickedCoordinate;
    });
  }

  render() {
    return (
      <div className="opticMapComponent">
        <div className="leftMapPanel">
          <div
            className="coordinateString"
            id="coordinateString"
            ref="coordinateString"
          ></div>
        </div>
        <div
          className="mapContainer"
          id="mapContainer"
          ref="mapContainer"
        ></div>
        <div className="rightMapPanel"></div>
      </div>
    );
  }
}

export default OpticMap;
