import React, { Component } from "react";

import { Map, View } from "ol";
import TileLayer from "ol/layer/Tile";
import OSM from "ol/source/OSM";
import { fromLonLat, transformExtent, transform } from "ol/proj";
import "./OpticMap.css";
import { Circle as CircleStyle, Fill, Stroke, Style } from "ol/style";
import Feature from "ol/Feature";
import Point from "ol/geom/Point";
// import Group from "ol/layer/Group";
import VectorLayer from "ol/layer/Vector";
import VectorSource from "ol/source/Vector";
import switchesArray from "./res/switches.json";

class OpticMap extends Component {
  constructor(props) {
    super(props);

    this.state = {
      map: null,
      coordinates: "coordinates",
    };

    this.handleZoom = this.handleZoom.bind(this);
    handleSwitchesClick = this.handleSwitchesClick.bind(this);
    this.switchesCheckBoxRef = React.createRef();
  }

  featureList = [];
  vectorSource = new VectorSource({
    features: featureList,
  });
  switchesLayer = new VectorLayer({
    source: vectorSource,
    visible: true,
    style: new Style({
      image: new CircleStyle({
        radius: 3.8,
        stroke: new Stroke({ color: "black", width: 1 }),
        fill: new Fill({ color: "tomato" }),
      }),
    }),
  });

  handleSwitchesClick() {
    this.setState({
      switchesLayerVisible: !this.state.switchesLayerVisible,
    });
  }

  handleZoom(zoomSize) {
    if (zoomSize < 12.8) {
      this.switchesLayer.setStyle(
        new Style({
          image: new CircleStyle({
            radius: 1,
            stroke: new Stroke({ color: "black", width: 1 }),
            fill: new Fill({ color: "tomato" }),
          }),
        })
      );
    } else if (zoomSize < 14) {
      this.switchesLayer.setStyle(
        new Style({
          image: new CircleStyle({
            radius: 2.5,
            stroke: new Stroke({ color: "black", width: 1 }),
            fill: new Fill({ color: "tomato" }),
          }),
        })
      );
    } else {
      this.switchesLayer.setStyle(
        new Style({
          image: new CircleStyle({
            radius: 4,
            stroke: new Stroke({ color: "black", width: 1 }),
            fill: new Fill({ color: "tomato" }),
          }),
        })
      );
    }
  }
  componentDidMount() {
    // map settings
    const extentView = transformExtent(
      [
        20.969893 /** min x */,
        57.540673 /** min y */,
        28.413477 /** max x */,
        59.894102 /** max y */,
      ],
      "EPSG:4326",
      "EPSG:3857"
    );
    const extentLayer = transformExtent(
      [
        16.581885 /** min x */,
        55.671002 /** min y */,
        32.794556 /** max x */,
        61.543774 /** max y */,
      ],
      "EPSG:4326",
      "EPSG:3857"
    );

    let map = new Map({
      target: this.refs.mapContainer,
      layers: [
        new TileLayer({
          source: new OSM(),
          extent: extentLayer,
          zIndex: 0,
        }),
      ],
      view: new View({
        center: fromLonLat([28.185281, 59.374348]),
        zoom: 14,
        extent: extentView,
        minZoom: 7.5,
      }),
    });

    // map settings

    //map feature settings

    switchesArray.map((data) => {
      let featureElement = new Feature({
        geometry: new Point(fromLonLat(data.coordinates)),
        address: data.address,
        ipAddress: data.ipAddress,
      });

      featureList.push(featureElement);
      console.log(featureElement.getKeys());
    });

    const switchesCheckBoxElement = this.switchesCheckBoxRef;

    // switchesCheckBoxElement.current.onclick = () => {
    //   console.log(this.switchesLayer.visible);
    // };
    //map feature settings

    //layers settings
    map.addLayer(this.switchesLayer);
    //layers settings
    // get coordinates by click on map
    map.on(
      "click",
      function (pointOfClick) {
        const clickedCoordinate = transform(
          pointOfClick.coordinate,
          "EPSG:3857",
          "EPSG:4326"
        );
        let coordinateString =
          clickedCoordinate[0].toFixed(6) +
          "," +
          clickedCoordinate[1].toFixed(6);
        this.setState({ coordinates: coordinateString });

        map.forEachFeatureAtPixel(pointOfClick.pixel, function (
          feature,
          layer
        ) {
          console.log(feature.getProperties().address);
          console.log(feature.getProperties().ipAddress);
        });
      }.bind(this)
    );
    // change features size on zoom change
    map.getView().on("propertychange", (e) => {
      switch (e.key) {
        case "resolution":
          this.handleZoom(map.getView().getZoom());
          console.log(map.getView().getZoom());

          break;
      }
    });

    this.setState({
      map: map,
      switchesLayerVisible: switchesCheckBoxElement.current.checked,
    });
  }

  render() {
    return (
      <div className="opticMapComponent">
        <div className="leftMapPanel">
          <div
            className="coordinateString"
            id="coordinateString"
            ref={this.coordinateRef}
          >
            <p>{this.state.coordinates}</p>
          </div>

          <div className="featureLayers">
            <div className="layerCheckBox">
              <input
                type="checkbox"
                value="switchesLayer"
                className="switch"
                onClick={this.handleSwitchesClick()}
                ref={this.switchesCheckBoxRef}
                defaultChecked="true"
              />

              <div>switches</div>
            </div>

            <div className="layerCheckBox">
              <input type="checkbox" value="clutchesLayer" className="switch" />
              <div>optic clutches</div>
            </div>

            <div className="layerCheckBox">
              <input type="checkbox" value="cableLayer" className="switch" />
              <div>optic Cable</div>
            </div>
          </div>
          <div className="featureInfo" id="featureInfo" ref="featureInfo"></div>
        </div>
        <div
          className="mapContainer"
          id="mapContainer"
          ref="mapContainer"
        ></div>
        <div className="rightMapPanel"></div>
      </div>
    );
  }
}

export default OpticMap;
