import React, { Component } from "react";

import { Map, View } from "ol";
import TileLayer from "ol/layer/Tile";
import OSM from "ol/source/OSM";
import { fromLonLat, transformExtent, transform } from "ol/proj";
import "./OpticMap.css";
// import Style from "ol/style/Style";
import Feature from "ol/Feature";
import Point from "ol/geom/Point";
// import Group from "ol/layer/Group";
import VectorLayer from "ol/layer/Vector";
import VectorSource from "ol/source/Vector";
import switchesArray from "./res/switches.json";

class OpticMap extends Component {
  componentDidMount() {
    const extentView = transformExtent(
      [
        20.969893 /** min x */,
        57.540673 /** min y */,
        28.413477 /** max x */,
        59.894102 /** max y */,
      ],
      "EPSG:4326",
      "EPSG:3857"
    );
    const extentLayer = transformExtent(
      [
        16.581885 /** min x */,
        55.671002 /** min y */,
        32.794556 /** max x */,
        61.543774 /** max y */,
      ],
      "EPSG:4326",
      "EPSG:3857"
    );

    let map = new Map({
      target: this.refs.mapContainer,
      layers: [
        new TileLayer({
          source: new OSM(),
          extent: extentLayer,
          zIndex: 0,
        }),
      ],
      view: new View({
        center: fromLonLat([28.185281, 59.374348]),
        zoom: 14,
        extent: extentView,
        minZoom: 7.5,
      }),
    });
    let marker = new Feature({
      geometry: new Point(fromLonLat([28.185281, 59.374348])),
    });

    let marker1 = new Feature({
      geometry: new Point(fromLonLat([28.185453, 59.374122])),
    });

    var featureList = [];

    switchesArray.map((data) => {
      let featureElement = new Feature({
        geometry: new Point(fromLonLat(data.coordinates)),
        name: data.coordinates,
      });
      featureList.push(featureElement);
      console.log(featureElement.getKeys());
    });

    let vectorSource = new VectorSource({
      features: featureList,
    });

    let markerVectorLayer = new VectorLayer({
      source: vectorSource,
    });

    map.addLayer(markerVectorLayer);

    const coordinateContainerElement = this.refs.coordinateString;
    const featureInfoElement = this.refs.featureInfo;
    this.setState({
      map: map,
    });

    // get coordinates by click on map
    map.on("click", function (pointOfClick) {
      const clickedCoordinate = transform(
        pointOfClick.coordinate,
        "EPSG:3857",
        "EPSG:4326"
      );
      coordinateContainerElement.innerHTML =
        clickedCoordinate[0].toFixed(6) +
        "," +
        clickedCoordinate[1].toFixed(6) +
        "," +
        map.getView().getZoom();
      map.forEachFeatureAtPixel(pointOfClick.pixel, function (feature, layer) {
        featureInfoElement.innerHTML = feature.getKeys("name");
      });
    });
  }

  render() {
    return (
      <div className="opticMapComponent">
        <div className="leftMapPanel">
          <div
            className="coordinateString"
            id="coordinateString"
            ref="coordinateString"
          >
            coordinates
          </div>
          <div className="featureLayers">
            <div className="layerCheckBox">
              <input
                type="checkbox"
                name="featureLayerCheckbox"
                value="switchesLayer"
                className="switch"
              />
              <span className="slider"></span>
              <div>switches</div>
            </div>

            <div className="layerCheckBox">
              <input
                type="checkbox"
                name="featureLayerCheckbox"
                value="clutchesLayer"
                className="switch"
              />
              <span className="slider"></span>
              <div>optic clutches</div>
            </div>

            <div className="layerCheckBox">
              <input
                type="checkbox"
                name="featureLayerCheckbox"
                value="cableLayer"
                className="switch"
              />
              <span className="slider"></span>
              <div>optic Cable</div>
            </div>
          </div>
          <div className="featureInfo" id="featureInfo" ref="featureInfo"></div>
        </div>
        <div
          className="mapContainer"
          id="mapContainer"
          ref="mapContainer"
        ></div>
        <div className="rightMapPanel"></div>
      </div>
    );
  }
}

export default OpticMap;
