import React, { Component } from "react";

import { Map, View } from "ol";
import TileLayer from "ol/layer/Tile";
import OSM from "ol/source/OSM";
import { fromLonLat, transformExtent, transform } from "ol/proj";
import "ol/style";
import "./OpticMap.css";
import Feature from "ol/Feature";
import Point from "ol/geom/Point";
import Group from "ol/layer/Group";
// import VectorContext from "ol/render/VectorContext";
import VectorLayer from "ol/layer/Vector";
import VectorSource from "ol/source/Vector";

class OpticMap extends Component {
  componentDidMount() {
    const point_feature = new Feature({});
    const point_geom = new Point([28.191968, 59.376725]);
    point_geom.transform([28.191968, 59.376725], "EPSG:3857", "EPSG:4326");
    point_feature.setGeometry(point_geom);

    const extentView = transformExtent(
      [
        20.969893 /** min x */,
        57.540673 /** min y */,
        28.413477 /** max x */,
        59.894102 /** max y */
      ],
      "EPSG:4326",
      "EPSG:3857"
    );
    const extentLayer = transformExtent(
      [
        16.581885 /** min x */,
        55.671002 /** min y */,
        32.794556 /** max x */,
        61.543774 /** max y */
      ],
      "EPSG:4326",
      "EPSG:3857"
    );

    let switchesLayer = new VectorLayer({
      source: new VectorSource()
    });
    let clutchesLayer = new VectorLayer({
      source: new VectorSource()
    });
    let cableLayer = new VectorLayer({
      source: new VectorSource()
    });
    const layerGroup = new Group({
      layers: [switchesLayer, clutchesLayer, cableLayer]
    });

    let map = new Map({
      target: this.refs.mapContainer,
      layers: [
        new TileLayer({
          source: new OSM(),
          extent: extentLayer,
          features: [point_feature]
        })
      ],
      view: new View({
        center: fromLonLat([28.185281, 59.374348]),
        zoom: 14,
        extent: extentView,
        minZoom: 7.5
      })
    });
    map.addLayer(layerGroup);

    const coordinateContainerElement = this.refs.coordinateString;
    this.setState({
      map: map
    });
    // get coordinates by click on map
    map.on("click", function(pointOfClick) {
      const clickedCoordinate = transform(
        pointOfClick.coordinate,
        "EPSG:3857",
        "EPSG:4326"
      );
      coordinateContainerElement.innerHTML =
        clickedCoordinate[0].toFixed(6) + "," + clickedCoordinate[1].toFixed(6);
    });
  }

  render() {
    return (
      <div className="opticMapComponent">
        <div className="leftMapPanel">
          <div
            className="coordinateString"
            id="coordinateString"
            ref="coordinateString"
          >
            coordinates
          </div>
          <div className="featureLayers">
            <div className="layerCheckBox">
              <input
                type="checkbox"
                name="featureLayerCheckbox"
                value="switchesLayer"
                className="switch"
              />
              <span className="slider"></span>
              <div>switches</div>
            </div>

            <div className="layerCheckBox">
              <input
                type="checkbox"
                name="featureLayerCheckbox"
                value="clutchesLayer"
                className="switch"
              />
              <span className="slider"></span>
              <div>optic clutches</div>
            </div>

            <div className="layerCheckBox">
              <input
                type="checkbox"
                name="featureLayerCheckbox"
                value="cableLayer"
                className="switch"
              />
              <span className="slider"></span>
              <div>optic Cable</div>
            </div>
          </div>
        </div>
        <div
          className="mapContainer"
          id="mapContainer"
          ref="mapContainer"
        ></div>
        <div className="rightMapPanel"></div>
      </div>
    );
  }
}

export default OpticMap;
