import React, { Component } from "react";
import { Map, View } from "ol";
import TileLayer from "ol/layer/Tile";
import OSM from "ol/source/OSM";
import { fromLonLat, transformExtent, transform } from "ol/proj";
import "./OpticMap.css";
import { Circle as CircleStyle, Fill, Stroke, Style } from "ol/style";
import Feature from "ol/Feature";
import Point from "ol/geom/Point";
import { Control, defaults } from "ol/control";
import VectorLayer from "ol/layer/Vector";
import VectorSource from "ol/source/Vector";
import switchesArray from "./res/switches.json";
import opticsArray from "./res/optics.json";
import LineString from "ol/geom/LineString";

class OpticMap extends Component {
  constructor(props) {
    super(props);

    this.state = {
      map: null,
      coordinates: "coordinates",
      displayedFeatures: null,
      featureInfo: {
        address: null,
        ipAddress: null,
        info: null,
        locationInfo: null,
      },
    };

    this.handleZoom = this.handleZoom.bind(this);

    this.setSwitchesToRightControlPanel = this.setSwitchesToRightControlPanel.bind(
      this
    );

    this.setOpticsToRightControlPanel = this.setOpticsToRightControlPanel.bind(
      this
    );

    this.initMapComponent = this.initMapComponent.bind(this);
  }

  initMapComponent() {
    const extentView = transformExtent(
      [
        20.969893 /** min x */,
        57.540673 /** min y */,
        28.413477 /** max x */,
        59.894102 /** max y */,
      ],
      "EPSG:4326",
      "EPSG:3857"
    );
    const extentLayer = transformExtent(
      [
        16.581885 /** min x */,
        55.671002 /** min y */,
        32.794556 /** max x */,
        61.543774 /** max y */,
      ],
      "EPSG:4326",
      "EPSG:3857"
    );
    const switchesVisible = (function (Control) {
      function switchesVisible() {
        const button = document.createElement("button");
        button.innerHTML = "switches";

        var element = document.createElement("div");
        element.className = "switches-visible ol-unselectable ol-control";
        element.appendChild(button);

        Control.call(this, {
          element: element,
        });

        button.addEventListener(
          "click",
          this.handleSwitchesVisible.bind(this),
          false
        );
      }

      if (Control) switchesVisible.__proto__ = Control;
      switchesVisible.prototype = Object.create(Control && Control.prototype);
      switchesVisible.prototype.constructor = switchesVisible;

      switchesVisible.prototype.handleSwitchesVisible = function handleSwitchesVisible() {
        map
          .getLayers()
          .getArray()
          .map((layer) => {
            if (layer.getClassName() === "switchesLayer") {
              layer.setVisible(!layer.getVisible());
            }
          });
      };

      return switchesVisible;
    })(Control);

    const opticsVisible = (function (Control) {
      function opticsVisible() {
        const button = document.createElement("button");
        button.innerHTML = "optics";

        var element = document.createElement("div");
        element.className = "optics-visible ol-unselectable ol-control";
        element.appendChild(button);

        Control.call(this, {
          element: element,
        });

        button.addEventListener(
          "click",
          this.handleOpticsVisible.bind(this),
          false
        );
      }

      if (Control) opticsVisible.__proto__ = Control;
      opticsVisible.prototype = Object.create(Control && Control.prototype);
      opticsVisible.prototype.constructor = opticsVisible;

      opticsVisible.prototype.handleOpticsVisible = function handleOpticsVisible() {
        map
          .getLayers()
          .getArray()
          .map((layer) => {
            if (layer.getClassName() === "opticsLayer") {
              layer.setVisible(!layer.getVisible());
            }
          });
      };

      return opticsVisible;
    })(Control);

    const map = new Map({
      target: this.refs.mapContainer,
      layers: [
        new TileLayer({
          source: new OSM(),
          extent: extentLayer,
          zIndex: 0,
        }),
      ],
      view: new View({
        center: fromLonLat([28.185281, 59.374348]),
        zoom: 14,
        extent: extentView,
        minZoom: 7.5,
      }),
      controls: defaults({
        attributionOptions: {
          target: this.refs.mapContainer,
        },
      }).extend([new switchesVisible(), new opticsVisible()]),
    });
    var switchFeatureArray = [];
    var opticsFeatureArray = [];

    switchesArray.map((data) => {
      let featureElement = new Feature({
        geometry: new Point(fromLonLat(data.coordinates)),
        address: data.address,
        ipAddress: data.ipAddress,
        info: data.info,
        locationInfo: data.locationInfo,
      });
      switchFeatureArray.push(featureElement);
    });

    opticsArray.map((data) => {
      let coordinatesArray = [];
      data.coordinates.map((coordinate) => {
        coordinatesArray.push(fromLonLat(coordinate));
      });
      let featureElement = new Feature({
        geometry: new LineString(coordinatesArray),
        address: data.address,
        ipAddress: data.ipAddress,
        info: data.info,
        locationInfo: data.locationInfo,
      });

      opticsFeatureArray.push(featureElement);
    });

    let opticsLayerSource = new VectorSource({
      features: opticsFeatureArray,
    });

    this.opticsLayer = new VectorLayer({
      className: "opticsLayer",
      source: opticsLayerSource,
      visible: true,
      style: new Style({
        stroke: new Stroke({
          width: 2,
          color: "blue",
        }),
      }),
    });

    let switchesLayerSource = new VectorSource({
      features: switchFeatureArray,
    });

    this.switchesLayer = new VectorLayer({
      className: "switchesLayer",
      source: switchesLayerSource,
      visible: true,
      style: new Style({
        image: new CircleStyle({
          radius: 3.8,
          stroke: new Stroke({ color: "black", width: 1 }),
          fill: new Fill({ color: "tomato" }),
        }),
      }),
    });

    //layers with network equipment

    //map feature settings

    //layers settings
    map.addLayer(this.switchesLayer);
    map.addLayer(this.opticsLayer);
    //layers settings
    //click on map
    map.on(
      "click",
      function (pointOfClick) {
        const clickedCoordinate = transform(
          pointOfClick.coordinate,
          "EPSG:3857",
          "EPSG:4326"
        );

        let coordinateString =
          clickedCoordinate[0].toFixed(6) +
          "," +
          clickedCoordinate[1].toFixed(6);
        this.setState({ coordinates: coordinateString });

        map.forEachFeatureAtPixel(
          pointOfClick.pixel,
          function (feature, layer) {
            this.setState({
              featureInfo: {
                address: feature.getProperties().address,
                ipAddress: feature.getProperties().ipAddress,
                info: feature.getProperties().info,
                locationInfo: feature.getProperties().locationInfo,
              },
            });
          }.bind(this)
        );
      }.bind(this)
    );

    // change features size on zoom change
    map.getView().on("propertychange", (e) => {
      switch (e.key) {
        case "resolution":
          this.handleZoom(map.getView().getZoom());
          break;
      }
    });
    this.setState({
      map: map,
    });
  }

  componentDidMount() {
    this.initMapComponent();
  }

  setSwitchesToRightControlPanel() {
    this.setState({
      displayedFeatures: switchesArray,
    });
  }
  setOpticsToRightControlPanel() {
    this.setState({
      displayedFeatures: opticsArray,
    });
  }

  handleRightPanelItemClick(item) {
    if (item.ipAddress === null) {
      this.state.map.getView().animate(
        {
          center: fromLonLat(item.coordinates[0]),
        },
        { zoom: 17.5 }
      );
    } else {
      this.state.map
        .getView()
        .animate({ center: fromLonLat(item.coordinates) }, { zoom: 19 });
    }
    this.setState({
      featureInfo: {
        address: item.address,
        ipAddress: item.ipAddress,
        info: item.info,
        locationInfo: item.locationInfo,
      },
    });
  }

  handleZoom(zoomSize) {
    if (zoomSize < 12.8) {
      this.switchesLayer.setStyle(
        new Style({
          image: new CircleStyle({
            radius: 1,
            stroke: new Stroke({ color: "black", width: 1 }),
            fill: new Fill({ color: "tomato" }),
          }),
        })
      );
      this.opticsLayer.setStyle(
        new Style({
          stroke: new Stroke({
            width: 0.5,
            color: "blue",
          }),
        })
      );
    } else if (zoomSize < 14) {
      this.switchesLayer.setStyle(
        new Style({
          image: new CircleStyle({
            radius: 2.5,
            stroke: new Stroke({ color: "black", width: 1 }),
            fill: new Fill({ color: "tomato" }),
          }),
        })
      );
      this.opticsLayer.setStyle(
        new Style({
          stroke: new Stroke({
            width: 2,
            color: "blue",
          }),
        })
      );
    } else {
      this.switchesLayer.setStyle(
        new Style({
          image: new CircleStyle({
            radius: 5,
            stroke: new Stroke({ color: "black", width: 1 }),
            fill: new Fill({ color: "tomato" }),
          }),
        })
      );
      this.opticsLayer.setStyle(
        new Style({
          stroke: new Stroke({
            width: 4,
            color: "blue",
          }),
        })
      );
    }
  }
  render() {
    console.log(this.state);
    return (
      <div className="opticMapComponent">
        <div className="leftMapPanel">
          <div className="coordinateString" id="coordinateString">
            {this.state.coordinates}
          </div>

          <div className="objectButtons">
            <div className="objectButton">
              <div
                className="switchesButton"
                onClick={this.setSwitchesToRightControlPanel}
              >
                switches
              </div>
            </div>

            <div className="objectButton">
              <div
                className="opticCableButton"
                onClick={this.setOpticsToRightControlPanel}
              >
                optic Cable
              </div>
            </div>
          </div>

          <div className="featureInfo">
            {this.state.featureInfo.address !== null ? (
              <div className="featureInfoElement">
                {this.state.featureInfo.address}
              </div>
            ) : (
              <div className="hidden"></div>
            )}

            {this.state.featureInfo.ipAddress !== null ? (
              <div className="featureInfoElement">
                {this.state.featureInfo.ipAddress}
              </div>
            ) : (
              <div className="hidden"></div>
            )}

            {this.state.featureInfo.info !== null ? (
              <div className="featureInfoElement">
                {this.state.featureInfo.info}
              </div>
            ) : (
              <div className="hidden"></div>
            )}

            {this.state.featureInfo.locationInfo !== null ? (
              <div className="featureInfoElement">
                {this.state.featureInfo.locationInfo}
              </div>
            ) : (
              <div className="hidden"></div>
            )}
          </div>
        </div>
        <div className="mapContainer" ref="mapContainer"></div>
        <div className="rightMapPanel">
          <div className="displayedFeatures">
            {this.state.displayedFeatures !== null ? (
              this.state.displayedFeatures.map((feature) => {
                return (
                  <li
                    className="displayedFeature"
                    key={feature.coordinates}
                    onClick={this.handleRightPanelItemClick.bind(this, feature)}
                  >
                    {feature.address}
                  </li>
                );
              })
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default OpticMap;
